import { Component, OnInit, ViewChild } from '@angular/core';
import { IonicModule, IonModal } from '@ionic/angular';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';

interface User {
  id: string;
  username: string;
  age: number;
  location?: string;
  profilePicture: string;
  online: boolean;
  gender: 'fem' | 'desfem' | 'trans';
  sexualOrientation: 'lesbian' | 'bisexual' | 'pansexual';
  bio?: string;
  glFavorite?: string;
}

@Component({
  selector: 'app-elo',
  templateUrl: './elo.page.html',
  styleUrls: ['./elo.page.scss'],
  standalone: true,
  imports: [IonicModule, CommonModule]
})
export class EloPage implements OnInit {
  @ViewChild('profileModal') profileModal!: IonModal;
  
  users: User[] = [
    {
      id: '1',
      username: '@marina',
      age: 25,
      location: 'São Paulo, SP',
      profilePicture: 'assets/placeholder-profile.jpg',
      online: true,
      gender: 'fem',
      sexualOrientation: 'lesbian',
      bio: 'Apaixonada por música e arte. Procurando conexões genuínas.',
      glFavorite: 'Juliana Paes'
    },
    {
      id: '2',
      username: '@carol',
      age: 28,
      location: 'Rio de Janeiro, RJ',
      profilePicture: 'assets/placeholder-profile.jpg',
      online: false,
      gender: 'desfem',
      sexualOrientation: 'bisexual',
      bio: 'Amo viajar e conhecer lugares novos.',
      glFavorite: 'Bruna Marquezine'
    },
    // Adicione mais usuários mock aqui
  ];

  selectedUser: User | null = null;

  constructor(private router: Router) {}

  ngOnInit() {
    // Aqui iremos carregar os usuários do backend quando implementarmos
  }

  showUserProfile(user: User) {
    this.selectedUser = user;
    this.profileModal.present();
  }

  async onLike(user: User, event?: Event) {
    if (event) {
      event.stopPropagation(); // Previne a abertura do modal ao clicar no botão
    }
    
    try {
      // Aqui implementaremos a lógica de like quando tivermos o backend
      console.log('Liked user:', user.username);
    } catch (error) {
      console.error('Error liking user:', error);
    }
  }

  async onChat(user: User, event?: Event) {
    if (event) {
      event.stopPropagation();
    }
    
    try {
      // Navegação para o chat com o usuário
      this.router.navigate(['/chat', user.id]);
    } catch (error) {
      console.error('Error starting chat:', error);
    }
  }

  async onReport(user: User, event?: Event) {
    if (event) {
      event.stopPropagation();
    }
    
    try {
      // Aqui implementaremos a lógica de denúncia quando tivermos o backend
      console.log('Reported user:', user.username);
    } catch (error) {
      console.error('Error reporting user:', error);
    }
  }
}
<ion-header>
  <ion-toolbar>
    <ion-title>Elo</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-grid>
    <ion-row>
      <ion-col size="6" *ngFor="let user of users">
        <ion-card [class.offline]="!user.online" (click)="showUserProfile(user)">
          <ion-img [src]="user.profilePicture" [alt]="user.username"></ion-img>
          <div class="status-indicator" [class.online]="user.online"></div>
          
          <ion-card-header>
            <ion-card-subtitle>{{ user.age }} anos</ion-card-subtitle>
            <ion-card-title>{{ user.username }}</ion-card-title>
          </ion-card-header>

          <ion-card-content>
            <ion-row class="action-buttons">
              <ion-col size="4">
                <ion-button fill="clear" color="tertiary" (click)="onLike(user, $event)">
                  <ion-icon name="heart-outline"></ion-icon>
                </ion-button>
              </ion-col>
              <ion-col size="4">
                <ion-button fill="clear" color="tertiary" (click)="onChat(user, $event)">
                  <ion-icon name="chatbubble-outline"></ion-icon>
                </ion-button>
              </ion-col>
              <ion-col size="4">
                <ion-button fill="clear" color="tertiary" (click)="onReport(user, $event)">
                  <ion-icon name="flag-outline"></ion-icon>
                </ion-button>
              </ion-col>
            </ion-row>
          </ion-card-content>
        </ion-card>
      </ion-col>
    </ion-row>
  </ion-grid>

  <!-- Modal de perfil -->
  <ion-modal #profileModal>
    <ng-template>
      <ion-header>
        <ion-toolbar>
          <ion-title>Perfil</ion-title>
          <ion-buttons slot="end">
            <ion-button (click)="profileModal.dismiss()">
              <ion-icon name="close"></ion-icon>
            </ion-button>
          </ion-buttons>
        </ion-toolbar>
      </ion-header>

      <ion-content *ngIf="selectedUser">
        <ion-card>
          <ion-img [src]="selectedUser.profilePicture"></ion-img>
          <ion-card-header>
            <ion-card-title>{{ selectedUser.username }}</ion-card-title>
            <ion-card-subtitle>
              {{ selectedUser.age }} anos • {{ selectedUser.location }}
            </ion-card-subtitle>
          </ion-card-header>

          <ion-card-content>
            <ion-chip color="primary">{{ selectedUser.gender }}</ion-chip>
            <ion-chip color="secondary">{{ selectedUser.sexualOrientation }}</ion-chip>
            
            <p class="bio">{{ selectedUser.bio }}</p>
            <p class="gl-favorite" *ngIf="selectedUser.glFavorite">
              Meu GL favorito é: {{ selectedUser.glFavorite }}
            </p>

            <ion-row class="action-buttons">
              <ion-col size="6">
                <ion-button expand="block" color="primary" (click)="onLike(selectedUser)">
                  <ion-icon slot="start" name="heart"></ion-icon>
                  Curtir
                </ion-button>
              </ion-col>
              <ion-col size="6">
                <ion-button expand="block" color="secondary" (click)="onChat(selectedUser)">
                  <ion-icon slot="start" name="chatbubble"></ion-icon>
                  Conversar
                </ion-button>
              </ion-col>
            </ion-row>
          </ion-card-content>
        </ion-card>
      </ion-content>
    </ng-template>
  </ion-modal>
</ion-content>
