import { Component, Input } from '@angular/core';
import { IonicModule } from '@ionic/angular';
import { CommonModule } from '@angular/common';
import { NavigationService } from '../../services/navigation.service';
import { AuthService } from '../../services/auth.service';
import { UiService } from '../../services/ui.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
  standalone: true,
  imports: [IonicModule, CommonModule]
})
export class HeaderComponent {
  @Input() title: string = '';

  constructor(
    private navigationService: NavigationService,
    private authService: AuthService,
    private uiService: UiService
  ) {}

  async onLogout() {
    const confirmed = await this.uiService.showConfirm(
      'Logout',
      'Are you sure you want to logout?'
    );

    if (confirmed) {
      try {
        await this.uiService.showLoading('Logging out...');
        await this.authService.logout();
        await this.uiService.showSuccess('Logged out successfully');
      } catch (error) {
        console.error('Logout error:', error);
        await this.uiService.showError('Failed to logout');
      } finally {
        await this.uiService.hideLoading();
      }
    }
  }

  navigateToFeed() {
    this.navigationService.navigateToFeed();
  }

  navigateToElo() {
    this.navigationService.navigateToElo();
  }
}
<ion-header>
  <ion-toolbar>
    <ion-title>{{ title }}</ion-title>

    <ion-buttons slot="end">
      <ion-button (click)="navigateToFeed()">
        <ion-icon slot="icon-only" name="newspaper-outline"></ion-icon>
      </ion-button>
      
      <ion-button (click)="navigateToElo()">
        <ion-icon slot="icon-only" name="people-outline"></ion-icon>
      </ion-button>

      <ion-button (click)="onLogout()">
        <ion-icon slot="icon-only" name="log-out-outline"></ion-icon>
      </ion-button>
    </ion-buttons>
  </ion-toolbar>

  <!-- Barra de navegação secundária -->
  <ion-toolbar class="sub-header">
    <ion-segment value="feed" mode="ios">
      <ion-segment-button value="feed" (click)="navigateToFeed()">
        <ion-icon name="newspaper"></ion-icon>
        <ion-label>Feed</ion-label>
      </ion-segment-button>

      <ion-segment-button value="elo" (click)="navigateToElo()">
        <ion-icon name="people"></ion-icon>
        <ion-label>Elo</ion-label>
      </ion-segment-button>
    </ion-segment>
  </ion-toolbar>
</ion-header>
